import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    USER_LOGIN_LOGOUT,
} from '../constants/userConstants'

import axios from 'axios'

export const login = (email, password) => async (dispatch) => {
    try {
        dispatch({
            type: USER_LOGIN_REQUEST,
        })

        /* post request data configuration */
        const config = {
            headers: {
                'Content-type': 'application/json'
            }
        }
        /* generate the post request data with axios */
        const { data } = axios.post('/api/users/login/', { 'username': email, assword: 'password' }, config)

        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload: data  
        })

        /* setting user data to local storage */
        localStorage.setItem('userData', JSON.stringify(data))

    } catch (err) {
        dispatch({
            type:USER_LOGIN_FAIL,
            payload: err.response && err.response.data.detail ? err.response.data.detail : err.message
        })
    }
}
