import React, { useState, useEffect} from 'react'
import { Link, useParams, useNavigate } from 'react-router-dom'
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap'
// react redux import for global state handling+API calls
import { useDispatch, useSelector } from 'react-redux'
import { listEventDetails, listRaffleTickets, createRaffleTicket } from '../reduxState/actions/eventActions'
// components
import Loader from '../components/Loader'
import Message from '../components/Message'

function EventView() {
    const {id} = useParams()
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')

    const dispatch = useDispatch()

    const eventDetails = useSelector(state => state.eventDetails)
    const { loading, event, error } = eventDetails

    useEffect(() => {
        dispatch(listEventDetails(id))
    }, [dispatch, id])

    const submitHandler = (e) => {
        console.log('submitted raffle ticket')
        // e.preventDefault()
        // setMessage('')

        // if(password !== confirmPassword) {
        //     setMessageVariant('warning')
        //     setMessage('Passwords do not match.')
        // } else if (password !== '' && password.length < 8){
        //     setMessageVariant('warning')
        //     setMessage('Password must be at least 8 characters long.')
        // } else {
        //     dispatch(updateUserData({
        //         'id': user._id,
        //         'name': name,
        //         'email': email,
        //         'password': password
        //     }))
        //     setMessageVariant('success')
        //     setMessage('Your profile was successfully updated.')
        // }

    }

    return (
        <div>
            <Link to='/' className='btn btn-light my-3'>Return</Link>
            {
                loading ? <Loader />
                        : error ? <Message variant='warning' children={error} /> 
                                : (
                                    <Row>
                                        <Col sm={8}>
                                            <Row className='pb-5'>
                                                <Image src={event.image} alt={event.eventName} fluid /> 
                                            </Row>

                                            <Row>
                                                <ListGroup variant="flush">
                                                    <ListGroup.Item style={{}}>
                                                        <h3>{event.eventName}</h3>
                                                    </ListGroup.Item>

                                                    <ListGroup.Item>
                                                        Category: {event.category}
                                                    </ListGroup.Item>

                                                    <ListGroup.Item>
                                                        Description: {event.description}
                                                    </ListGroup.Item> 
                                                </ListGroup>
                                            </Row>

                                        </Col>    
                                        <Col sm={1}/>
                                        <Col sm={3}>
                                            <Card>
                                                <Card.Title>Enter Raffle Ticket!</Card.Title>
                                                <Card.Body>
                                                    <Form onSubmit={submitHandler}>
                                                        <Form.Group className='my-4' controlId='name'>
                                                            <Form.Label>Name</Form.Label>
                                                            <Form.Control 
                                                                required
                                                                type='name' 
                                                                placeholder='Enter Name' 
                                                                value={name} 
                                                                onChange={(e) => setName(e.target.value)}
                                                            />
                                                        </Form.Group>

                                                        <Form.Group className='my-4' controlId='email'>
                                                            <Form.Label>Email Address</Form.Label>
                                                            <Form.Control 
                                                                required
                                                                type='email' 
                                                                placeholder='Enter Email' 
                                                                value={email} 
                                                                onChange={(e) => setEmail(e.target.value)}
                                                            />
                                                        </Form.Group>

                                                        <Button type='submit' variant='primary'>Submit</Button>

                                                    </Form>
                                                </Card.Body>
                                            </Card>
                                        </Col>

                                    </Row>
                                )

            }

        </div>
    )
}

export default EventView
